<script>
import axios from 'axios';

export default {
    data() {
        return {
            isGradient: false,
            formation: [],
            workShopInformation: [],
            culturesInformation: [],
            formationWorking: [
                { humanasisGradient: false },
                { financierasisGradient: false },
                { comercialesisGradient: false },
                { logisticaisGradient: false },
                { genericaisGradient: false }
            ],
            workShops: [
                { presencialisGradient: false },
                { remotoisGradient: false },
                { hibridoisGradient: false },
                { indiferenteisGradient: false }
            ],
            cultures: [
                { emocionalesisGradient: false },
                { conocimientoisGradient: false },
                { remotoisGradient: false },
                { produccionisGradient: false },
                { intuitivaisGradient: false },
                { indiferenteisGradient: false }
            ],
        };
    },
    methods: {
        async submitData() {
            try {
                const payload = {
                    formation: this.formation,
                    workShops: this.workShopInformation,
                    cultures: this.culturesInformation,
                    userId: 1,
                };
                const response = await axios.post('http://localhost:3000/tipeworkin', payload);
                console.log('Respuesta de la API:', response.data);
            } catch (error) {
                console.error('Error al enviar los datos:', error);
                alert('Hubo un error al enviar los datos');
            }
        },
        //CAPTURA DATOS DE Tipo de formación y empleo que buscas:
        toggleGradientHumanas(title) {
            var addTitle = false
            switch (title) {
                case 'Áreas humanas':
                    this.formationWorking[0].humanasisGradient = !this.formationWorking[0].humanasisGradient;
                    addTitle = this.formationWorking[0].humanasisGradient
                    break;

                case 'Áreas Financieras':
                    this.formationWorking[0].financierasisGradient = !this.formationWorking[0].financierasisGradient;
                    addTitle = this.formationWorking[0].financierasisGradient
                    break;

                case 'Área Comerciales':
                    this.formationWorking[0].comercialesisGradient = !this.formationWorking[0].comercialesisGradient;
                    addTitle = this.formationWorking[0].comercialesisGradient
                    break;

                case 'Áreas de Logística':
                    this.formationWorking[0].logisticaisGradient = !this.formationWorking[0].logisticaisGradient;
                    addTitle = this.formationWorking[0].logisticaisGradient
                    break;

                case 'Genérica':
                    this.formationWorking[0].genericaisGradient = !this.formationWorking[0].genericaisGradient;
                    addTitle = this.formationWorking[0].genericaisGradient
                    break;

                default:
                    break;
            }

            if (addTitle) {
                this.addTitleToDataHumanas(title);
            } else {
                this.removeTitleFromDataHumanas(title);
            }
        },
        addTitleToDataHumanas(title) {
            const exists = this.formation.some((item) => item.title === title);
            if (!exists) {
                this.formation.push({ title });
            }
        },
        removeTitleFromDataHumanas(title) {
            this.formation = this.formation.filter((item) => item.title !== title);
        },
        //CAPTURA DATOS DE Opciones de jornadas:
        toggleGradientJornadas(title) {
            var addTitle = false
            switch (title) {
                case 'Presencial':
                    this.workShops[0].presencialisGradient = !this.workShops[0].presencialisGradient;
                    addTitle = this.workShops[0].presencialisGradient
                    break;

                case 'Remoto':
                    this.workShops[0].remotoisGradient = !this.workShops[0].remotoisGradient;
                    addTitle = this.workShops[0].remotoisGradient
                    break;

                case 'Hibrido':
                    this.workShops[0].hibridoisGradient = !this.workShops[0].hibridoisGradient;
                    addTitle = this.workShops[0].hibridoisGradient
                    break;

                case 'Me es indiferente':
                    this.workShops[0].indiferenteisGradient = !this.workShops[0].indiferenteisGradient;
                    addTitle = this.workShops[0].indiferenteisGradient
                    break;

                default:
                    break;
            }

            if (addTitle) {
                this.addTitleToDataJornadas(title);
            } else {
                this.removeTitleFromDataJornadas(title);
            }
        },
        addTitleToDataJornadas(title) {
            const exists = this.workShopInformation.some((item) => item.title === title);
            if (!exists) {
                this.workShopInformation.push({ title });
            }
        },
        removeTitleFromDataJornadas(title) {
            this.workShopInformation = this.workShopInformation.filter((item) => item.title !== title);
        },
        //CAPTURA DATOS DE Qué tipos de culturas te gustan más:
        toggleGradientCulturales(title) {
            var addTitle = false
            switch (title) {
                case 'Emocionales':
                    this.cultures[0].emocionalesisGradient = !this.cultures[0].emocionalesisGradient;
                    addTitle = this.cultures[0].emocionalesisGradient
                    break;

                case 'Conocimiento':
                    this.cultures[0].conocimientoisGradient = !this.cultures[0].conocimientoisGradient;
                    addTitle = this.cultures[0].conocimientoisGradient
                    break;

                case 'Remoto':
                    this.cultures[0].remotoisGradient = !this.cultures[0].remotoisGradient;
                    addTitle = this.cultures[0].remotoisGradient
                    break;

                case 'Producción':
                    this.cultures[0].produccionisGradient = !this.cultures[0].produccionisGradient;
                    addTitle = this.cultures[0].produccionisGradient
                    break;

                case 'Intuitiva':
                    this.cultures[0].intuitivaisGradient = !this.cultures[0].intuitivaisGradient;
                    addTitle = this.cultures[0].intuitivaisGradient
                    break;

                case 'Me es indiferente':
                    this.cultures[0].indiferenteisGradient = !this.cultures[0].indiferenteisGradient;
                    addTitle = this.cultures[0].indiferenteisGradient
                    break;

                default:
                    break;
            }

            if (addTitle) {
                this.addTitleToDataCulturales(title);
            } else {
                this.removeTitleFromDataCulturales(title);
            }
        },
        addTitleToDataCulturales(title) {
            const exists = this.culturesInformation.some((item) => item.title === title);
            if (!exists) {
                this.culturesInformation.push({ title });
            }
        },
        removeTitleFromDataCulturales(title) {
            this.culturesInformation = this.culturesInformation.filter((item) => item.title !== title);
        },
    },
};
