<script>
import NavbarTitle from "../../components/NavbarTitle.vue";
import ImageNavbar from "../../components/ImageNavbar.vue";
import SectionInformation from "../../components/SectionInformation.vue";
import ConfigurationWorkingView from "../../components/viewinformation/ConfigurationWorkingView.vue";
import AcademicFormation from "../../components/viewinformation/AcademicFormation.vue";
import ExperienceWork from "../../components/viewinformation/ExperienceWork.vue";
import PermiosAndLogros from "../../components/viewinformation/PermiosAndLogros.vue";
import Footer from "../../components/Footer.vue";
import FooterImages from "../../components/FooterImages.vue";
import FONDOMARCA from "../../assets/FONDOMARCA.png";
import axios from "axios";

export default {
    data() {
        return {
            FONDOMARCA,
            userId: 1,
            generalInformation: {},
        };
    },
    components: {
        NavbarTitle,
        ImageNavbar,
        SectionInformation,
        ConfigurationWorkingView,
        AcademicFormation,
        ExperienceWork,
        PermiosAndLogros,
        Footer,
        FooterImages,
    },
    methods: {
        async mountedConfiguration() {
            await this.$refs.configurationComponentView.fetchData();
            await this.$refs.academicFormationData.fecthFormation();
            await this.$refs.experienceWorkData.fecthExperience();
            await this.$refs.premioLogrosData.fecthExperienceKills();
            const response = await axios.get(`https://seustalentback.onrender.com/generalinformation/${this.userId}`);
            this.generalInformation = response.data;
        },
    },
    mounted() {
        this.mountedConfiguration()
    },
};

</script>